const axios = require('axios');

const live = require('../utils/live');
const verify = require('../utils/verify');

async function liveCred(hostName, isPlus, qSetId, newDateISOString, t_a, gameMode, cookie) {
    const liveData = await live(hostName, isPlus, qSetId, newDateISOString, t_a, gameMode, cookie);

    const idToken = await verify(liveData.fbToken);

    return {
        _idToken: idToken,
        _id: liveData.id
    };
};

module.exports = liveCred